<?php
/**
 * SVG icons.
 *
 * @package seo_one_click_theme
 */

/**
 * Return required SVG Icon.
 *
 * @param string $icon_name
 * @return svg
 */
function seo_one_click_theme_get_svg_icon( $icon_name = '', $width = '', $height = '',  $color = '' ) {
  if ( '' != $icon_name ) {
    $icons = array_svg_icons( $width, $height, $color );

    foreach ( $icons as $icon_id => $icon ) {
      if ( $icon_name == $icon_id ) {
        return $icon;
      }
    }
  }
}

/**
